mapper.update:
update articles
  set title = #{title}, contents = #{contents}, isNotice = #{isNotice}, isComment = #{isComment}
  ,modifyDate = current_timestamp
where
  seq = #{seq} and bm_seq = #{bm_seq}

update articles set deletedate = current_timestamp, isDelete = 1
where
seq = #{seq} and bm_seq = #{bm_seq}

mapper.select:
select * from articles
where isDelete = 0
and seq = #{seq} and bm_seq = #{bm_seq}
